// unselect to apply filter
// #define NORMAL

float[] kernel = float[9](1.0/9.0,1.0/9.0,1.0/9.0,
                          1.0/9.0,1.0/9.0,1.0/9.0,
                          1.0/9.0,1.0/9.0,1.0/9.0);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy;

    vec3 acc = vec3(0.0);

    for(int i = 0; i < 3; i++){
        for(int j = 0; j < 3; j++){
            vec2 realRes = uv + vec2(i - 1, j - 1);
     		acc += texture(iChannel0, realRes / iResolution.xy).rgb * kernel[i * 3 + j];
        }
    }
    #ifdef NORMAL
		fragColor = texture(iChannel0, uv / iResolution.xy);
    #else
     	fragColor = vec4(acc, 1.0);
    #endif
}
