// Andor Salga 2013

const float PI = 3.141592658;
const float TAU = 2.0 * PI;
const vec4 yellow = vec4(1,1,0,1);

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
  vec2 pos = vec2(fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;
  pos *= 1.1;

  float rad = length(pos);
  float angle = atan(pos.y, pos.x);

  vec4 tex = vec4( texture(iChannel0, pos));

  fragColor = vec4(0,0,0,1);

  fragColor += (yellow * tex) * step(rad, 0.05);
  fragColor += (yellow * tex) * step(0.45, rad);

  float ma = mod(angle, TAU/3.0);
  if(ma > PI/3.0 && rad > 0.1){
	fragColor = yellow * tex ;
  }

  float dimBottom = step(rad, 0.5) * smoothstep(0.0, 2.0,  1.0 + pos.y * 2.0);
  float dimTop = step(rad, 0.5) * smoothstep(2.0, 0.0, 0.5 + pos.y * 2.0);

  fragColor *= vec4(dimBottom);
  fragColor *= vec4(dimTop);


}
