void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


	vec2 reso = iResolution.xy;

	reso.x += 0.0;
	reso.y += 235.0;

	vec2 uv = fragCoord.xy / reso;
	fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);

	uv.x += 0.3;
	uv.y += 0.3;

	vec2 position =  -1.5 + 2.5 * uv;
	vec2 z = position;

		for(int i = 0; i < 20; i++)
		{ 	
			position.y *= cos(iTime*-0.43);  					
			position.x *= sin(iTime*-0.57);

			//z.x -= sin(iTime*atan(0.33,8.0));
			z.x -= sin((iTime/10.0)*atan(0.33,8.0));

			//z.x -= atan(iTime,sin(5.5));

			z.y *= atan(iTime,atan(-16.03,iTime));

			z = position + vec2(
            		z.x*z.x-z.y*z.y,
            		2.0*z.x*z.y);
            	if(length(z) > 1.5){
            		fragColor = vec4(0.0, 0.0, 0.0, 1.0);

            	}
            }
}
