void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec2 delta = vec2( (sin(iTime)+sin(iTime/3.0)+sin(iTime/13.7)*0.2+sin(iTime/1.0))/60.0, 0 );
	vec4 txt = texture(iChannel0,uv.xy);
	txt.r = texture(iChannel0,uv.xy+delta ).r;
	txt.b = texture(iChannel0,uv.xy-delta ).b;
	fragColor = txt;
}
