void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = texture(iChannel0, uv);
}

int redState(vec2 pos) {
    vec2 uv = pos / iResolution.xy;

    if (texture(iChannel0, uv).x > 0.01f) {
        return 1;
    }

    return 0;
}

int blueState(vec2 pos) {
    vec2 uv = pos / iResolution.xy;

    if (texture(iChannel0, uv).z > 0.01f) {
        return 1;
    }

    return 0;
}

float hash(float p) {
  const float cHashM = 43758.54;
  return fract (sin (p) * cHashM);
}

vec3 init(float id) {
    float h = hash(id);
    if (h < 0.3333) {
    	return vec3(1.0f, 0.0f, 0.0f);
    } else if (h < 0.6666) {
    	return vec3(0.0f, 0.0f, 1.0f);
    }
    return vec3(0.0f);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    float id = fragCoord.y * iResolution.x + fragCoord.x;

    if (iFrame == 0) {
        fragColor = vec4(init(id), 1.0f);
        return;
    }

    vec2 pixelSize = iResolution.xy;

    int redOwnState = redState(fragCoord);
    int blueOwnState = blueState(fragCoord);


    vec3 futureState = vec3(0.0f);

    int redCount = 0;
    redCount += redState(fragCoord + vec2(1.0f, 0.0f));
    redCount += redState(fragCoord + vec2(1.0f, 1.0f));
    redCount += redState(fragCoord + vec2(0.0f, 1.0f));
    redCount += redState(fragCoord + vec2(-1.0f, 1.0f));
    redCount += redState(fragCoord + vec2(-1.0f, 0.0f));
    redCount += redState(fragCoord + vec2(-1.0f, -1.0f));
    redCount += redState(fragCoord + vec2(0.0f, -1.0f));
    redCount += redState(fragCoord + vec2(1.0f, -1.0f));

    int blueCount = 0;
    blueCount += blueState(fragCoord + vec2(1.0f, 0.0f));
    blueCount += blueState(fragCoord + vec2(1.0f, 1.0f));
    blueCount += blueState(fragCoord + vec2(0.0f, 1.0f));
    blueCount += blueState(fragCoord + vec2(-1.0f, 1.0f));
    blueCount += blueState(fragCoord + vec2(-1.0f, 0.0f));
    blueCount += blueState(fragCoord + vec2(-1.0f, -1.0f));
    blueCount += blueState(fragCoord + vec2(0.0f, -1.0f));
    blueCount += blueState(fragCoord + vec2(1.0f, -1.0f));


    if (redOwnState == 1 && redCount != 0 && blueCount == 0) {
        if (redCount >= 2 && redCount <= 3) {
        	futureState.x = 1.0f;
        }
    } else if (blueOwnState == 1 && blueCount != 0 && redCount == 0) {
        if (blueCount >= 2 && blueCount <= 3) {
        	futureState.z = 1.0f;
        }
    } else if (redOwnState == 1 && redCount + 1 > blueCount) {
        if (redCount >= 2 && redCount <= 3) {
        	futureState.x = 1.0f;
        }
    } else if (blueOwnState == 1 && blueCount + 1 > redCount) {
    	 if (blueCount >= 2 && blueCount <= 3) {
        	futureState.z = 1.0f;
        }
    } else if (redOwnState == 0 && blueOwnState == 0) {
        if (redCount + blueCount == 3) {
            if (redCount > blueCount) {
            	futureState.x = 1.0f;
            } else {
            	futureState.z = 1.0f;
            }
        }
    } else if (blueOwnState == 1) {
		futureState.x = 1.0f;
    } else {
    	futureState.z = 1.0f;
    }

    fragColor = vec4(futureState, 1.0f);
}
