void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 nuv = uv * 2.0 - 1.0;

    float t = iTime;
    float bpm = 1.0 / 60.0 * 123.0 * 3.1415926535 * 2.0;

    float beat = texture(iChannel0, vec2(0.0, 0.25)).x;

    beat = max(beat - 0.30, 0.0);
    beat = pow(beat, 2.0) * 30.0;
    beat = beat * sin(t * bpm);

    vec2 nuv0 = nuv * vec2(cos(t * bpm * nuv.y), sin(t * bpm * nuv.x)) * 2.0 * beat;

    float c0 = (nuv0.x * nuv0.y) * beat + nuv.x * nuv.y;

    fragColor.x = beat * 0.30;
    fragColor.y = beat * 0.15 + 0.3;
    fragColor.z = c0  * sin(t * bpm) * 0.2 + beat * 0.2 + 0.2;

    float s0 = pow(1.0 - length(nuv), 4.0);
    float s1 = pow(1.0 - length(nuv + vec2(cos(t / 8.0 * bpm) * (0.2 + 0.1 * beat), sin(t / 8.0 * bpm) * 0.2 * beat)), 4.0);
    float s2 = pow(1.0 - length(nuv + vec2(cos(t / 8.0 * bpm + 3.2) * 0.2 * beat, sin(t / 8.0 * bpm + 3.2) * (0.2 + 0.1 * beat))), 4.0);

    fragColor.xyz += vec3(1,1,1) * s0;
    fragColor.xyz += vec3(1,1,1) * s1 * s2;
}
