void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = texture(iChannel0, uv);
}

float mdist(vec2 a, vec2 b){vec2 v = abs(a - b); return v.x + v.y;}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if(iFrame == 0) fragColor = vec4(mod(fragCoord, 2.0), mod(fragCoord + 1.0, 2.0));
    else {
        vec4 samp1 = texture(iChannel0, (fragCoord + vec2(1.0, 0.0)) / iResolution.xy);
        vec4 samp2 = texture(iChannel0, (fragCoord - vec2(1.0, 0.0)) / iResolution.xy);
        vec4 samp3 = texture(iChannel0, (fragCoord + vec2(0.0, 1.0)) / iResolution.xy);
        vec4 samp4 = texture(iChannel0, (fragCoord - vec2(0.0, 1.0)) / iResolution.xy);
        fragColor = mod(samp1 + samp2 + samp3 + samp4, 2.0);
        if(mdist(iMouse.xy, fragCoord) < 10.0 && iMouse.xy == iMouse.zw && iMouse.xy != vec2(0)){
            fragColor = vec4(1.0, 0.0, 1.0, 1.0);
        } 
    }
}
