void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 uv2 = uv;
    uv.y = step(uv.y, 0.5);

	vec4 audio = texture(iChannel0, uv);

    fragColor = audio.rrrr;
    fragColor.a = 1.0;

    fragColor.r = step(uv2.y*2.0, audio.r);
    fragColor.b = step(uv2.y-0.50, audio.r);

    fragColor *= fragColor*4.0-2.0;
}
