#define TAU 6.2831853

float rand(float x){return fract(sin(x*133.)+450.);}

float map(vec3 p) {
    float R=3.;
    float d=abs(length(p.xy)-R)-.1;

    float msl=2.;
    float zi=floor((p.z)/msl);
    float a=atan(p.y,p.x);
    float at=TAU*rand(zi)+iTime*(rand(zi)-.5)*4.;
    float az=mod(at-a+TAU*.5,TAU)-TAU*.5;
    d=max(d,-abs(mod(p.z+.5*msl,msl)-msl*.5)+.2*msl);
    d=max(d,-max((abs(az)-rand(zi+.5)-.1)*R, abs(mod(p.z,msl)-.5*msl)-.4*msl));

    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv =(fragCoord.xy -.5* iResolution.xy)/iResolution.y;
    vec3 ro=vec3(uv,iTime*2.),rd=normalize(vec3(uv,1.)),mp=ro;
    int i;for (i=0;i<30;++i){float md=map(mp);if(md<.001)break;mp+=rd*md;}
	fragColor = vec4(length(mp-ro)*.05);
}
