#define PI 3.14159265358

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float x = uv.x;
    float y = uv.y;
    x += iTime * 0.25;

    float wavelength = 100.0;
    float amplitude = 25.0;

    y += sin(x * PI * (iResolution.x / (wavelength / 2.0))) * (amplitude / iResolution.y);;

    fragColor = texture(iChannel0, vec2(uv.x, y));
}
