void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float skip_ahead_seconds = 140.;

	vec2 uv = ((skip_ahead_seconds + iTime / 200.0) / 1.0) * ((fragCoord.xy - (iResolution.xy / 2.0)) / iResolution.y);

    //uv = floor(uv * 2.);
    float value = (uv.x * sign(uv.y) + uv.y * sign(uv.x)) * sin(uv.x) * sin(uv.y);
    float color = sin(value) * 3.0;
    color = color * sign(uv.x) * sign(uv.y); // revision 2

    float low = abs(color);
    float med = abs(color) - 1.0;
    float high = abs(color) - 2.0;
    if(color > 0.0) {
	  fragColor = vec4(med*2., high, high,1.0);
    } else {
      fragColor = vec4(high, high, med*2.,1.0);
    }
}
