float median( float x )
{
    return 2.0 * x;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float rotation = sin(iTime);

    vec2 relativePoint = fragCoord.xy / iResolution.xy;

    float kx = rotation;
    float ky = 1.0 - kx;

    float redness = relativePoint.x * kx + relativePoint.y * ky;
    float greenness = 1.0 - redness;

    vec4 gradient = vec4(
        median(redness)
      , median(greenness)
      , 0.0
      , 0.5
    );

	vec2 uv = fragCoord.xy / iResolution.xy;
    fragColor =
        texture(iChannel0, uv)
      * gradient;
}
