void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 src = texture(iChannel0, fragCoord / iResolution.xy).rgb;

    // float lum = (src.r + src.g + src.b) / 3.;
    float lum = length(src) / 3.;

	// fragColor = lum >= texture(iChannel1, fragCoord / 8.).r ? vec4(1,1,1,1):vec4(0,0,0,1);
	fragColor = vec4(lum >= texture(iChannel1, fragCoord / 8.).r);
}
