// with mix, smoothstep and min functions suggested by FabriceNeyret2

void mainImage( out vec4 c, in vec2 uv )
{
    uv /= iResolution.x; uv.y -= .281;
    float t = iTime;

    vec2 b; // ball coordinates

    b.x = mod(t / 1.7, 1.88) + .03; // ball horizontal position
    b.y = mod(t / 2.5, 1.08) - .27; // ball vertical position

    b = min(b, vec2 (1.94,.54) - b); // reverse ball direction when needed

    // draw ball
    c = vec4(smoothstep(1., .9, 70. * distance(uv, b))); // color

    // left carriage position
    float l = mix(b.y, sin(t * 3.1) * .2, b.x);

    // right carriage position
    float r = mix(cos(t * 3.7) / 4.5, b.y * .9, b.x);

    if (
        (uv.x < .02 && abs(l - uv.y) < .06) // draw left carrige
      ||
        (uv.x > .98 && abs(r - uv.y) < .06) // draw right carrige
       )
       c += 1.;
}


