float PI = 3.14159;


vec4 palette(float index){
  return vec4(0.5+0.5*sin(index*PI/0.5),0.5+0.5*sin((index*0.7*floor(iTime))*PI/0.5),0.5+0.5*sin((index* 14.2)*PI/0.5),1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = palette(floor(uv.x*uv.y*32.));
}

