vec3 rgb(float r, float g, float b) {
	return vec3(r / 255.0, g / 255.0, b / 255.0);
}


vec4 circle(vec2 uv, vec2 pos, float rad, vec3 color) {
	float d = length(pos - uv) - rad;
	float t = clamp(d, 0.0, 1.0);
	return vec4(color, 1.0 - t);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

	vec2 uv = fragCoord.xy;
	vec2 center = iResolution.xy * 0.5;
	float radius = 0.25 * iResolution.y;

	vec4 layer1 = vec4(rgb(0.0, 0.0, 0.0), 1.0);

	fragColor = layer1;


    radius = 200.0;

    //  The main shimmering effect:
    for (float i = 0.0; i < 10.0; i += 1.0)
    {	
        vec3 color = rgb(i*20.0, i*20.0, i*20.0);
        radius = radius - 20.0 - 10.0*sin(200.0*iTime);
        vec4 layer = circle(uv, center, radius, color);
		fragColor = mix(fragColor, layer, layer.a);
    }

    //  Creates a consistent center:
    radius = 20.0;
    vec3 color = rgb(200.0, 200.0, 200.0);
	vec4 layer = circle(uv, center, radius, color);
    fragColor = mix(fragColor, layer, layer.a);

    //  Creates a little oscillating planetoid:
    // function for controlling the motion (changes the center of the circle):
    //   vec2( A1*sin(f*iTime), A2*sin(f*iTime)
    //
    //   Changing A1 and A2 alters the 'angle' of the motion
    //   Changing f alters the frequency of the motion
    //     Play with them, see what happens.

    radius = 5.0;
    color = rgb(200.0, 200.0, 200.0);
	layer = circle(uv, center+vec2(50.0*sin(20.0*iTime),50.0*sin(20.0*iTime)), radius, color);
    fragColor = mix(fragColor, layer, layer.a);

    radius = 2.0;
    color = rgb(200.0, 200.0, 200.0);
	layer = circle(uv, center+vec2(-50.0*sin(1.0*iTime),150.0*sin(1.0*iTime)), radius, color);
    fragColor = mix(fragColor, layer, layer.a);    

}
