vec4 drawDot(vec2 fragCoord, vec4 backgroundColor, vec4 dotColor, vec2 xy)
{
	if((fragCoord.x > xy.x) && (fragCoord.x < xy.x + 1.0))
    {
        if((fragCoord.y > xy.y) && (fragCoord.y < xy.y + 1.0))
        {
            return dotColor;
        }
    }

    return backgroundColor;
}

vec4 drawLine(vec2 fragCoord, vec4 backgroundColor, vec4 lineColor, int f, int p1x, int p1y, int p2x, int p2y)
{
    int j = p1x;

    for(int i = p1y; i < p2y; i++)
    {
        backgroundColor = drawDot(fragCoord, backgroundColor, lineColor, vec2(i, j += (iTime / 10.0)));
    }

    return backgroundColor;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec4 backgroundColor = vec4(0, 0, 0, 1);

    backgroundColor = drawLine(fragCoord, backgroundColor, vec4(1, 1, 1, 1), vec2((0.5 * iResolution.x), (0.5 * iResolution.y)), vec2((0.5 * iResolution.x) + (20.0 * sin(iTime)), (0.5 * iResolution.y)) + (2.0 * sin(iTime)));

	fragColor = backgroundColor;
}
