void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 seedCoord;
    vec3 seedColor;

    vec4 read = texture(iChannel0, uv);

    DecodeData(read, seedCoord, seedColor);

    float distSeed = distance(fragCoord, seedCoord);
    float marker = (distSeed < 10.)?
        			2.:
    				1.;

    // Output to screen
    fragColor = vec4(seedColor
                     *marker
                     *distSeed/100.
                     , 1.);

    fragColor = .01*vec4(seedCoord-fragCoord, vec2(0.));
}

#define JFA_MAX_STEPS 10
#define FRAME_DELAY 1.


//--------------------------------------------------

vec4 JFAStep(vec2 fragCoord, int level)
{
    // current data in texture on fragCoord position
    vec4 ret = texture(iChannel0, fragCoord/iChannelResolution[0].xy);

    // big number so any real distance will be smaller than that
    float curDist = 999999999.;

    // that means the current data contains info of a nearby seed
    if( ret != vec4(0.) )
    {
        vec2 seedCoord;
		vec3 seedColor;

        DecodeData(ret, seedCoord, seedColor);

        curDist = distance(fragCoord, seedCoord);
    }

    vec2 offset = vec2(exp2(max(0., float(JFA_MAX_STEPS - level) ) ) );

    for(int x = -1; x <= 1; x++)
    {
    	for(int y = -1; y <= 1; y++)
        {
			vec4 read = texture(iChannel0, (fragCoord + vec2(x,y)*offset)/iChannelResolution[0].xy );
            if( read != vec4(0.) )
            {
                vec2 coord;
                vec3 color;

                DecodeData(read, coord, color);

                float dist = distance(coord, fragCoord);

                if( dist < curDist)
                {
                    ret = read;
                }
            }
        }
    }

    return ret;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    

    // Create seeds
    if(iFrame == 0)
    {
		if(Rand(fragCoord) > (1.- SEED_CHANCE))
        {
            fragColor = EncodeData(fragCoord, RandColor(fragCoord));
        }
        else
        {
            fragColor = vec4(0.);
        }

    }
    else
    {
        if(mod( float(iFrame), FRAME_DELAY) == 0.)
        {
        	fragColor = JFAStep(fragCoord, int(float(iFrame) / float(FRAME_DELAY)) );

        }
        else
        {
            fragColor = texture(iChannel0, fragCoord/iChannelResolution[0].xy);
        }
    }


}

// Recreating Jump flood algorithm for learning purposes
// Saw in https://www.shadertoy.com/view/Mdy3DK

#define SEED_CHANCE .0005
//============================================================
// Hash without sine from https://www.shadertoy.com/view/4djSRW
#define HASHSCALE1 .1031
float hash12(vec2 p)
{
	vec3 p3  = fract(vec3(p.xyx) * HASHSCALE1);
    p3 += dot(p3, p3.yzx + 19.19);
    return fract((p3.x + p3.y) * p3.z);
}
// copied the random functions...
//============================================================
// returns 0..1
float Rand(vec2 co)
{
    co.x += fract(iDate.w * 5.342);
    co.y += fract(iDate.w * 11.214);
    return hash12(co);
}

//============================================================
vec3 RandColor (vec2 co)
{
    return vec3
    (
        Rand(co),
        Rand(co*2.143),
        Rand(co*3.163)
	);
}

// Putting coordinate encoded in the color
vec4 EncodeData(vec2 coord, vec3 color)
{
	vec4 ret = vec4(0.);
	ret.xy = coord;
    ret.z = floor(color.x * 255.)*256. + floor(color.y * 255.);
    ret.w = floor(color.z * 255.);
	return ret;
}

// Getting the coordinate out of the color
void DecodeData(in vec4 data, out vec2 coord, out vec3 color )
{
	coord = data.xy;
    color.x = floor(data.z/256.)/255.;
    color.y = mod(data.z,256.)/255.;
    color.z = mod(data.w,256.)/255.;
}
