void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= iResolution.x / iResolution.y;

    vec3 color = vec3(0.0);

    vec3 TEXT_COLOR = vec3(1.0);
    float TEXT_SIZE = 0.08;

    vec2 LINE0_POSITION = vec2(0.1, 0.7);
    int LINE0[16] = int[](20, 8, 5, 0, 17, 21, 9, 3, 11, 38, 0, 2, 18, 15, 23, 14);
    for(int i = 0; i < 16; i++)
        color = character(TEXT_COLOR, color, LINE0[i], LINE0_POSITION + vec2(float(i) * (TEXT_SIZE + TEXT_SIZE/5.0), 0.0), TEXT_SIZE, uv);

    vec2 LINE1_POSITION = vec2(0.2, 0.45);
    int LINE1[14] = int[](6, 15, 24, 0, 10, 21, 13, 16, 19, 0, 15, 22, 5, 18);
    for(int i = 0; i < 14; i++)
        color = character(TEXT_COLOR, color, LINE1[i], LINE1_POSITION + vec2(float(i) * (TEXT_SIZE + TEXT_SIZE/5.0), 0.0), TEXT_SIZE, uv);

    vec2 LINE2_POSITION = vec2(0.3, 0.2);
    int LINE2[13] = int[](20, 8, 5, 0, 12, 1, 26, 25, 0, 4, 15, 7, 37);
    for(int i = 0; i < 13; i++)
        color = character(TEXT_COLOR, color, LINE2[i], LINE2_POSITION + vec2(float(i) * (TEXT_SIZE + TEXT_SIZE/5.0), 0.0), TEXT_SIZE, uv);

    fragColor = vec4(color, 1.0);
}

vec3 character(vec3 color, vec3 background, int character, vec2 position, float size, vec2 uv)
{
    if((uv.x > position.x && uv.x < position.x + size) && (uv.y > position.y && uv.y < position.y + size))
    {
        ivec2 pixel = ivec2(ceil((uv.x-position.x)/size*5.0)-1.0, ceil((1.0-(uv.y-position.y)/size)*5.0)-1.0);
        int bit_index = pixel.y*5 + pixel.x;
        int bit = (CHARS[character] >> (24 - bit_index))&1;
        if(bit > 0)
            return color;
    }
    return background;
}
