float circleDist(vec2 p, float radius)
{
	return length(p) - radius;
}


vec2 translate(vec2 p, vec2 t)
{
	return p - t;
}


float boxDist(vec2 p, vec2 size, float radius)
{
	size -= vec2(radius);
	vec2 d = abs(p) - size;
  	return min(max(d.x, d.y), 0.0) + length(max(d, 0.0)) - radius;
}


float fillMask(float dist)
{
	return clamp(-dist, 0.0, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float between = abs(sin(iTime*0.5));//0.3;//clamp(iTime*0.0001, 0.0, 1.0);
    vec2 p = fragCoord.xy;
    float C = circleDist(translate(p, vec2(300, 200)), 90.0);
	float S =  boxDist(translate(p, vec2(300, 200)), vec2(90, 90), 	10.0);

    float d = (1.0 - between)*S+C*between;
    fragColor = vec4(vec3(d),1.0);//mix(vec4(0), vec4(1), fillMask(d));//
}
