void mainImage(out vec4 o, vec2 p)
{
    o = vec4(0.);
    vec3 R = iResolution;
    p=(p-R.xy/2.)/min(R.x,R.y);
    p.y*=-1.;
    p.y+=2.8;

    float a = atan(p.x,p.y);
    float r = length(p);
    float ab = abs(a);

    if(ab<0.257)
        o+=1.;
    else if(ab>0.263)
        o+=.3;

    o*=vec4(0.18,0.60,0.,1);
    if(r>3.29)
        o = vec4(0.439, 0.20, 0.075,1.);
    if(r<2.4)
        o = vec4(0.2);

    //borders
    if(abs(r-2.4)<0.007 
       || abs(r-3.29)<0.007
      ) o*=0.;
}
