const float eps = 0.0001;

float f(vec2 p)
{
    float value = (texture(iChannel0, p) +
                   texture(iChannel0, p * 2.0) / 2.0 +
                   texture(iChannel0, p * 4.0) / 4.0 +
                   texture(iChannel0, p * 8.0) / 8.0 +
                   texture(iChannel0, p * 16.0) / 16.0 +
                   texture(iChannel0, p * 32.0) / 32.0 +
                   texture(iChannel0, p * 64.0) / 64.0 +
                   texture(iChannel0, p * 128.0) / 128.0 +
                   texture(iChannel0, p * 256.0) / 256.0).x;

    value /= 2.0 - 1.0 / 256.0;

    return pow(value, 10.0 * p.x);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float c = f(fragCoord / 1000.0);
    fragColor = vec4(c, c, c, 1.0);
}
