#define LINE_WIDTH .1

void drawLine(float y, vec2 uv, vec3 color, inout vec3 fc) {

    if(uv.y > y-LINE_WIDTH && uv.y < y+LINE_WIDTH) {

        fc = color;

    }

    fc = clamp(fc, 0., 1.);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = ((fragCoord - iResolution.xy * .5 )/ iResolution.y) * 10.;
	float x = uv.x;
    float y = 0.;
    vec3 fc = vec3(0.);

    //draw background
    drawLine(0., uv, vec3(.5), fc);
    //x = .5;
    //drawLine(x, uv, vec3(.5), fc);
    //x = uv.x;
    //finish background

   	y = sin(iTime*3.5 + x);
	drawLine(y, uv, vec3(1.), fc);

    y = cos(iTime*3.5 + x) * 5.;
    drawLine(y, uv, vec3(1.,0.,0.), fc);

    y = tan(-iTime + x);
    drawLine(y, uv, vec3(0.,1.,0.), fc);

    y = texture(iChannel0, vec2(x,.5+.5*sin(iTime))).r * 2. - 1.;
    drawLine(y, uv, vec3(0.,1.,1.), fc);

    //add more here
    //y = x;
    //drawLine(y, uv, vec3(1.,1.,0.), fc);

	fragColor = vec4(fc,1.);
}
