void mainImage( out vec4 O, vec2 U )
{
    float v = U.x,
          H = iResolution.y;
    if (U.y < H*.33) v = .5;   // bottom: for reference, set v=.5 before vec4

    if (U.y < H*.66) 
        O = vec4( v = .5 , v < 30. ? 0 : 1, 0, 1); // middle: test evaluated before vec4 !
    else
        O = vec4( v = .5 , v, v, 1);               // top: O.y evaluated correctly
}
