void mainImage( out vec4 O,  vec2 U ) {
    O = texelFetch(iChannel0,ivec2(U),0).wwww;
}

vec2 vv;
vec4 l (int a, int b) {return texture(iChannel0,(vec2(1,0)/iResolution.xy+vv+vec2(a,b)/iResolution.xy));}
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
		#define df vec4(.2,0.5,0.3,0.5)
		vv = fragCoord.xy/iResolution.xy;
    	vec4 x = l(0,0);
		if (x.z < 0. || x.x < 0.4) x.x = 0.;
		x.x = x.x+x.x*(-x.y+x.w);
		x.y = x.y*0.97 + 0.05*x.x - 0.2*x.x*x.z;
		x.w = x.w*0.93 + 0.47*x.x*x.z;
		x.z = x.z+0.05*(1.-x.z)-0.07*x.x;
		int b = mod(vv.y*iResolution.y,2.) < 1.?1:-1;
		x = x*(1.-df) + df/6.*(
			l(1,0)+l(0,1)+l(-1,0)+l(0,-1)
			+l(b,1) + l(b,-1)
			);

		if (vv.x*iResolution.x<2.&&vv.y<0.7&&vv.y>0.3) x.x = 0.;

		fragColor = x;
		if (iFrame < 3) fragColor = vec4(sin(12.*vv.x)*cos(12.*vv.y)>0.9?1:0,0,1,0);
}

