float Bub(vec2 uv,float scale)
{
    float time = iTime*0.75;
    uv.y-=time*2./scale;
    //uv.x += time*0.2;
    uv.x+=sin(uv.y+time*.95)*0.025;	///scale;
	uv*=scale;
    vec2 s=floor(uv);
    vec2 f=fract(uv);
    float k=3.0;
	vec2 p =.5+.35*sin(11.*fract(sin((s+scale)*mat2(7.0,3.0,6.0,5.0))*5.))-f;
    float d=length(p);
    k=min(d,k);
	k=smoothstep(0.0,k,sin(f.x+f.y)*0.01);
   	return k;
}

vec3 Bubbles(vec2 uv,float head,float d)
{
	float c = Bub(uv,30.)*.3;
	c+=Bub(uv,20.)*.5;
	c+=Bub(uv,15.)*.8;
	c+=Bub(uv,10.);
	c+=Bub(uv,8.);
	c+=Bub(uv,6.);
	c+=Bub(uv,5.);
    //vec3 background = vec3(0.3,0.3,0.3);
    vec3 background2 = vec3(1.0-d*d,0.3,0.35);
    float bub_brightness = 2.5+head+d;
    vec3 scol = background2 * bub_brightness; //vec3(1.0,1.0,1.0);
    scol = mix(background2,scol,c);
    return scol;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (2.*fragCoord.xy - iResolution.xy)/ iResolution.y;

    float d = length(uv);
    float head = 1.0-length(uv);
    d+=(0.5+sin(iTime*2.0)*0.5)*0.25;
    uv *= 0.25;
    vec3 col = Bubbles(uv*(d+0.5), head,d);
    fragColor =vec4(col,1.0);
}
