vec4 noise( in vec2 x ){return texture(iChannel0, x);}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //float _Mirrors = sin(iTime*0.1)*45.0;
    float _Mirrors = 15.0;
    float _Fill = sin(iTime*0.1)*sin(iTime);
    float t = iTime;

	vec2 uv =fragCoord.xy / iResolution.xy;


    vec2 p  = -1.0 + 2.0 *  (uv );  
    float a = atan(p.y,p.x);			
	float r = sqrt(dot(p,p));

    uv.x =t+ sin(_Mirrors*r+t) + _Fill*cos(t+_Mirrors*a);// _Mirrors*a/3.1416;			   
	uv.y = -t+ sin(_Mirrors*r+t) + _Fill*cos(t+_Mirrors*a);
    fragColor = noise(uv);

}
