void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pos = fragCoord.xy / iResolution.xy;

    // rainbow

    float a = (iTime * 10.0) + (pos.y * 2.0 * 3.14159);
    a += sin(pos.x + 4.0 * sin(iTime * 0.25)) * 20.0;

    float offset = (iTime * 0.1 * 2.0 * 3.14159) / 3.0;

    float r = (cos(a) + 1.0) * 0.5;
    float g = (cos(a + offset) + 1.0) * 0.5;
    float b = (cos(a + (offset * 2.0)) + 1.0) * 0.5;

    // vignette

    vec2 delta = pos - vec2(0.5,0.5);
    float d = length(delta);
    d *= 1.414213562373095;	// 1.0/sqrt(0.5)   
    d = pow(d,4.0);
    d = 1.0 - d;

    fragColor = vec4(vec3(r,g,b)*d,1.0);
}
