// [2TC 15] Mystery Mountains.
// Fractal mountains in 280 chars.
// Original by Dave Hoskins. : https://www.shadertoy.com/view/llsGW7#
vec3 HUEtoRGBZ(vec3 H)
  {
    float R = (abs(H * 6. - 3.) - 1.).r;
    float G = (2. - abs(H * 6. - 2.)).g;
    float B = (2. - abs(H * 6. - 4.)).r;
    return clamp(vec3(R,G,B), 0., 1.);
  }

vec3 HUEtoRGB(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

#define F t+=texture(iChannel0,.3+p.xw*s/6e3,-99.)/s;s+=s;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float T = iTime; 
    //vec4 p=fragCoord/(vec4(iResolution.xyz, iResolution.x))-.5);
    vec4 p; 
  	p.x = fragCoord.x/iResolution.x - .5; 
    p.y = fragCoord.y/iResolution.y - .5; 
    p.z = 0.0;
        p.w = 1.0;

    vec4 d=p*1.0;
    vec4 t;
    p.w += iTime*20.;
    d.y-=.1;//camera adjustment down
    d.x-=.1;

    vec4 color; 

    for(float i=1.0;i>=0.;i-=.02)
    {
        float s=1.5;
        t = d - d; 

        float uvXscaler = 5.; 
        t += 1.*(1.0 - texture(iChannel0,vec2(uvXscaler*p.x*s/60000. + .5, 0.0)).r/s) - .7 ;    
        t += texture(iChannel0,vec2(uvXscaler*p.x*s/60000. - .1, 0.0)).r/s  ;



       // s+=s;

      //  t+=texture(iChannel1,p.xw*s/6e3,-99.)/s;

        t+=texture(iChannel1,p.xw*s/6e3);
        //s+=s;


		color = vec4(0,0,0,0)*.1+d.x+t*i;

        float height = 4.0;//sin(T)*.01 + .7; 
        float yscaler = .0095;// (sin(T*10.)+3.)*.5*.01;

       // if(t.x>p.y*yscaler*(abs(.00500*p.x))*abs(sin(iTime)*.3 + 1.) + height)break;
        p += d * 100.0 ;//iTime - 1800.;
    } 

    color = vec4(dot(color.xyz,vec3(0.5,0.0,0.5)) * HUEtoRGB(vec3(p.x*p.y*0., 1.0 - color.r, 1.0 - color.y)), 1.0);

    if(t.x > 2.0)
        t *= 0.0; 

   // vec4( HUEtoRGB(vec3(0.0, 0.0, 1.0)), 1.0);
    fragColor = abs(t.r - t.w*0.2 + 0.*t.w)*vec4( HUEtoRGB(vec3(iTime*0.0001+ p.y*.001, 1.0 - color.x, 1.0 - color.y)), 1.0);


}
