#define PI 		3.14159265359
#define TWO_PI 	6.28318530718
vec2 origin = vec2(0.0, 0.0);
float smoothing = 0.0025;

float ring(vec2 t, float inner, float outer, float rotation)
{
    vec2 q = vec2(cos(rotation)*t.x + sin(rotation)*t.y, -sin(rotation)*t.x + cos(rotation)*t.y);
    float dist =  distance(q, origin);
    float grad = (atan(q.x, q.y) + PI) / (2.0 * PI);
    float circ =  smoothstep(inner-smoothing, inner+smoothing, dist) - (1.0 -smoothstep(outer+smoothing, outer-smoothing, dist));

    return circ * grad;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord)
{
	vec2 uv = 2.0 * vec2(fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;

    float inRing = 		ring(uv, 0.400, 0.450, iTime);    
    float outerRing =	ring(uv, 0.350, 0.395, -iTime);

    float final = inRing + outerRing;

	fragColor = vec4(final);
}
