//we now write smoothstep with only one letter; S!
#define S smoothstep 
vec3 white = vec3(1.0,1.0,1.0);
vec3 grey = vec3(0.5,0.5,0.5);
vec3 lightgrey = vec3(0.8,0.8,0.8);
vec3 darkgrey = vec3(0.2,0.2,0.2);

vec3 gear2(vec2 p, vec3 s, float spikenum, float thickness){

        float a = atan(p.y, p.x)+ iTime;
        float l = length(p)*2.0;
        float g = S(-.95, -.5, cos(a*spikenum))*0.06+0.5;

            //float gear = 1.0-S(g,g+0.03, l);   //takes waveform as bounds
            float midgear = 1.0-S(g-0.02,g+0.03, l*1.1);
            //float biggear = 1.0-S(g,g+0.0003, l);
            float xshole = 1.0-S(g,g+0.03, l*thickness);//or3



    vec3 mainplates = vec3(midgear-xshole)*vec3(1.0,1.0,1.0);
   // vec3 rims = vec3(gear-biggear)*vec3(0.3,0.0,1.0) ;
    //vec3 spokes3 = s;// vec3(.0,0.2,0.9)*spokes;  
    vec3 outcolor =  mainplates  + s;//spokes3;

    return outcolor;

    }

vec3 spokes1(vec2 p, float spokenum){
    float a = atan(p.y, p.x)+ iTime;
    float l = length(p)*2.0;

    float h = (abs(fract(a*spokenum+l*4.)*2.-1.)-0.240)*0.456+0.1828;
    float spokes = 1.0-S(h-0.09,h+0.03, l);
    vec3 spokes3 = vec3(.0,0.2,0.9)*spokes; 

    return spokes3; 
}

vec3 spokes2(vec2 p, float spokenum){
    float a = atan(p.y, p.x)+ iTime;
    float l = length(p)*2.0;

    float h =  pow(abs(fract(a*spokenum)*2.-0.848),-0.136)/5.992;
    float spokes = 1.0-S(h-0.09,h+0.03, l);
    vec3 spokes3 = vec3(.0,0.2,0.9)*spokes; 

    return spokes3; 
}

vec3 spokes3(vec2 p, float spokenum){
    float a = atan(p.y, p.x)+ iTime;
    float l = length(p)*2.0;

    float h = pow(abs(fract(a*spokenum)*2.000-1.000)+0.236,3.976)/6.976+.128;
    float spokes = 1.0-S(h-0.09,h+0.03, l);
    vec3 spokes3 = vec3(.0,0.2,0.9)*spokes; 

    return spokes3; 
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 pos = (fragCoord.xy*2.-iResolution.xy)/iResolution.y;

    vec3 color = darkgrey;


    //1.752 for 1
    //2.208 for2
    //1.752 for 3
    color+=gear2(vec2(pos.x + 1.2, pos.y+ 0.6), spokes1(vec2(pos.x + 1.2, pos.y+ 0.6),2.10/4.0), 10.0, 1.8);
    color+=gear2(vec2(pos.x + 0.0, pos.y+ 0.6), spokes2(vec2(pos.x + 0.0, pos.y+ 0.6),2.208), 20.0, 1.0);
    color+=gear2(vec2(pos.x - 1.2, pos.y+ 0.6), spokes3(vec2(pos.x - 1.2, pos.y+ 0.6),0.632), 40.0, 1.3);
    color+=gear2(vec2(pos.x + 1.2, pos.y+ 0.0), spokes1(vec2(pos.x + 1.2, pos.y+ 0.0),2.208*2.0), 20.0, 1.4);
    color+=gear2(vec2(pos.x + 0.0, pos.y+ 0.0), spokes2(vec2(pos.x + 0.0, pos.y+ 0.0),2.208), 5.0, 2.4);
    color+=gear2(vec2(pos.x - 1.2, pos.y+ 0.0), spokes3(vec2(pos.x - 1.2, pos.y+ 0.0),0.632), 25.0, 1.9);
    color+=gear2(vec2(pos.x + 1.2, pos.y- 0.6), spokes1(vec2(pos.x + 1.2, pos.y- 0.6),2.208), 35.0, 1.1);
    color+=gear2(vec2(pos.x + 0.0, pos.y- 0.6), spokes2(vec2(pos.x + 0.0, pos.y- 0.6),2.208), 15.0, 1.7);
    color+=gear2(vec2(pos.x - 1.2, pos.y- 0.6), spokes3(vec2(pos.x - 1.2, pos.y- 0.6),0.632), 3.0, 1.4);


	fragColor = vec4(color,1.0);
}


