void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 r = fragCoord.xy/iResolution.xy;  
    r.y = 1.0 - r.y;

    vec2 t = r;
    t.y = sin(t.x * 4. + iTime*2.);

    vec4 a = mix(texture(iChannel0,r.xy),vec4(1.), t.y);
    vec4 col = clamp(a,texture(iChannel0, r.xy), vec4(1.));

    fragColor = col;
}
