#define TINT vec4(0.8f, 0.4f, 0.4f, 1.0f)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec4 blurredTex = texture(iChannel0, uv);
    vec4 tex = texture(iChannel1, uv);
    // Output to screen
    fragColor = blurredTex * TINT + tex;
}

#define LUMINANCE_LIMIT 0.7
#define RED_LUMINANCE 0.299
#define GREEN_LUMINANCE 0.584
#define BLUE_LUMINANCE 0.114

float calculateLuminance(vec3 pixelColor){
    float r = RED_LUMINANCE * pixelColor.x;
    float g = GREEN_LUMINANCE * pixelColor.y;
    float b = BLUE_LUMINANCE * pixelColor.z;
	return r + g + b;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    vec4 bloomPixel = vec4(0.0f);
    vec4 tex = texture(iChannel0, uv);



    if(calculateLuminance(tex.xyz) > LUMINANCE_LIMIT){
    	bloomPixel += tex;
    }

    fragColor = bloomPixel;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    float kernel[9];
    kernel[0] = kernel[8] = 1.;
    kernel[1] = kernel[7] = 1.5;
    kernel[2] = kernel[6] = 2.;
    kernel[3] = kernel[5] = 2.5;
    kernel[4] = 4.;

    vec4 finalColor;
    float divisor;

    for(int i = -4; i <= 4; i++){
    	vec4 pixelColor = texture(iChannel0, (fragCoord + vec2(0, i)) / iResolution.xy );
        pixelColor *= kernel[i + 4];
        divisor += kernel[i + 4];
        finalColor += pixelColor;
    }

    fragColor = vec4(finalColor.x / divisor,
                     finalColor.y / divisor,
                     finalColor.z / divisor,
                     1.0);
}

