    ivec2 iU = ivec2(U); U = fract(U);
    iU.y += iU.y > 0 ? 5: 0;
    int d = iU.y / 6,
        y = iU.y % 6,
        l = ( ivec4( iDate.w/vec4(.1,1,60,3600) ) % ivec4(10,60,60,24) )[d],
        n = iU.x + 10*y; 
    O -= O;
    U.y>.1 && U.x > .1 && iU.x < 10 && n < l 
        ? O[(d+3)%4]++ : 0.; O += O.w;
}

