vec2 lissajous(float t){
    return 0.5*(iResolution.xy + iResolution.xy*vec2(sin(6.0*t + 5.0), cos(9.0*t)));
}

vec2 m(float t){return iMouse.xy==vec2(0) ? lissajous(t) : iMouse.xy;}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    fragColor = vec4(0);
    fragColor = texture(iChannel0, uv);
    if(distance(m(iTime), fragCoord) < 10.0) fragColor.r = 1.0;
    if(texture(iChannel1, vec2(32.5/256.0, 0.25)).x > 0.5) fragColor.g = 1.0;
    else fragColor.g = 0.0;
}

#define u i/iResolution.xy
#define T texture(iChannel0,u)

vec2 lissajous(float t){
    return 0.5*(iResolution.xy + iResolution.xy*vec2(sin(6.0*t + 5.0), cos(9.0*t)));
}

vec2 m(float t){return iMouse.xy==vec2(0) ? lissajous(t) : iMouse.xy;}

void mainImage(out vec4 o,vec2 i)
{
   o=vec4(0);
   vec4 tex2 = texture(iChannel2, u);
   if(distance(m(iTime), i)<10.)o=0.25*texture(iChannel1,u);
   else if(tex2.r > 0.5 || tex2.g > 0.5){
        o=T;
        i.x--;
        vec4 a=T;
        i+=vec2(1);
        a+=T;
        i.y-=2.;
        a+=T;
        i+=vec2(1);
        a+=T;
        o=sin(a);
   }
}
