//	modified version of https://www.shadertoy.com/view/XsXXDn


//#define VARIATION2 1

//	hash23() based on hash functions from Dave_Hoskins -- https://www.shadertoy.com/view/ltfGDs
vec3 hash23( vec2 p ) {
	p		= fract( p * vec2( 5.3983, 5.4427 ) );
    p		+= dot( p.yx, p.xy + vec2( 21.5351, 14.3137 ) );
	return fract( p.x * p.y * vec3( 95.4337, 97.597, 93.234 ) );
}

vec2 complex_multiply( vec2 a, vec2 b )		{ return vec2( a.x * b.x - a.y * b.y, a.x * b.y + a.y * b.x ); }	//	ac - bd, ad + bc

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	float t = ( sin( iTime / 2e1 ) + 1. ) * 2e1;
	vec2 r = iResolution.xy;
	vec3 c;
	float l=0., z=t, k=5.;
	for(int i=0;i<3;i++) {
		vec2 p		= floor( fragCoord.xy )/r;
		p			-= .5;
		p.x			*= r.x/r.y;
		z			+= .07;
#ifdef VARIATION2
        k			= log(iTime);
        z			+= k/1e2;
#endif
		l			= length(p);
        p			= complex_multiply( p, normalize( vec2( max( 0.01, sin( t ) + 1. ), max( 0.01, cos( t * l ) + 1. ) ) ) );
		p			+= p/l*(sin(z)+1.)*abs(sin(l*9.-z*2.));
		c[i]		= .01/length(abs(mod(p,1.)-.5));
	}
    c				= clamp( c, 0., 1. );
    c				= mix( k * c, vec3( .5 ), ( c.x + c.y + c.z ) / 4. );
    c 				+= hash23( fragCoord.xy ) / 2e2;	// dithering to remove banding -- iq did this with a texture https://www.shadertoy.com/view/ll2GD3

	fragColor=vec4( 1.2 * c, 1. );
}
