void mainImage( out vec4 O, vec2 U )
{
	vec2 R = iResolution.xy;
         U = ( U+U - R ) / R.y;

    float N = pow(1.2, iTime), // 200.
          p = 3.1416/N,
          a = mod( atan(U.y,U.x) + p, p+p) - p;         // 2Pi/N symmetry

    O = vec4( smoothstep( 3./R.y,0., length(U) * abs(sin(a)) ) ); 
}
