float v(float x, float y, float rot, float t) {

	float cx = cos(rot);
    float cy = sin(rot);

    return cos(cx * x * 80.0 + cy * y * 80.0 + t);
}

vec4 colorize_fire( float v ) 
{
	float _v = 1.0-v;	
	return vec4(0.0) - vec4( 0.5*_v, 0.2*_v, 0.0, 1.0 );
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	float vs = 0.0;

    for(int i = 1; i < 20; i++) {
   		float vt = v(float(i) * uv.x*0.055, float(i)* 0.055*uv.y, 3.1415 / float(i) * 250.0 , iTime); 
        vs += vt;
    }


 	vec4 col = colorize_fire(vs); // vec3(vs, vs, vs);

    // Output to screen
    fragColor = col;
}
