void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy - 0.5;
    vec2 perm = vec2(0.0,0.0);
    perm.x = uv.x*uv.x*0.5 + uv.x; 
    perm.y = uv.y*uv.x*0.5 + uv.y; 
	vec4 bg = vec4(texture(iChannel0,mix(perm+0.5, perm-0.5, 0.5*sin(iTime))));
    vec4 noise = vec4(texture(iChannel2,mix(uv+0.5, uv-0.5, 0.5*cos(iTime))));
    vec2 warriorOffset = uv+0.5;
    vec4 warrior = vec4(texture(iChannel1,warriorOffset, 1.0));

    fragColor = bg;
    warriorOffset.x += sin(iTime*1.1+uv.y*50.9)*0.01*sin(iTime*0.5);
    warriorOffset.y += cos(iTime*1.1+uv.x*50.9)*0.01*cos(iTime*0.3);

    if(warrior.y < 0.5 || length(warrior) > 1.3 )
    {
        if(warrior.x < 0.5 || warrior.y > 0.37){
            warrior.z = warrior.x = warrior.y;
        }
        fragColor = warrior;
        fragColor.xyz = noise.xyz;
    }

    fragColor.x = 0.5*sin(1.5*fragColor.x*3.14)+0.5;
    fragColor.y = 0.5*sin(1.5*fragColor.x*3.14+3.14/3.0)+0.5;
    fragColor.z = 0.5*sin(1.5*fragColor.x*3.14+2.0*3.14/3.0)+0.5;
}
