void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p1 = iMouse.xy / iResolution.xy;
    vec2 p2 = fragCoord.xy / iResolution.xy;
	float xDiff = p2.x - p1.x;
    float yDiff = p2.y - p1.y;

    float dist = sqrt(pow(p2.x - p1.x, 2.0) + pow(p2.y - p1.y, 2.0));

    if(mod(atan(yDiff, xDiff) * (180.0 / 3.1415), 40.0) < mod(iTime * 10.0, 30.0)){
     	dist += 0.1 + sin(iTime);   
    }

    if( mod(dist, 0.3 * sin(iTime * 2.2) ) < 0.1 * cos(iTime * 2.2)){
     	dist = 0.0;   
    }


	fragColor = vec4(1.0 - dist, 1.0-dist, 1.0 - dist,1.0);
}
