struct circle {vec2 pos; float rad;};


circle myCircle = circle(vec2(0.0,0.0),0.25);


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xx - 0.5;
    uv.y += 0.1;
    myCircle.rad = 0.25*(sin(iTime)*0.5+1.0);
    float circdist = distance(uv, myCircle.pos);
    fragColor += 1.0;
    fragColor = vec4( texture(iChannel0,uv + 0.5));
    if(circdist < myCircle.rad)
    {
        fragColor += vec4( texture(iChannel0,myCircle.pos-(uv-0.5)));
    }
    else
    {
        fragColor -= vec4( texture(iChannel1,myCircle.pos-(uv-0.5)));
    }
    fragColor = mix(fragColor,
                    vec4(sin(iTime), sin(iTime+3.14/3.0), sin(iTime+2.0*3.14/3.0), 1.0),
                    0.5+sin(iTime/10.0)/2.0);
}
