const float pixel_w = 4.;
const float pixel_h = 4.;
const float color_r = 0.1;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float vx_offset = 0.5;

	vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 c = texture(iChannel0, uv);
    vec3 tc = vec3(1., 0., 0.);

    vec2 coord = vec2(0.,0.);

    if(uv.x < (vx_offset-0.001))
    {
        float dx = pixel_w*(1./iResolution.x);
        float dy = pixel_h*(1./iResolution.y);
        coord = vec2(dx*floor(uv.x/dx),
                     dy*floor(uv.y/dy));
    }
    else if (uv.x>=(vx_offset+0.001))
    {
        coord = uv;
    }

    tc = texture(iChannel0, coord).rgb;

    // Ramp colors
    float r = 4.;

    tc.r = floor(tc.r * r)/r;
    tc.g = floor(tc.g * r)/r;
    tc.b = floor(tc.b * r)/r;


	fragColor = vec4(tc, 1.0);


}
