void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv = vec2(1.) - 2. * uv;  // [-1..1]
    uv *= vec2(3.0);  // [-3..3]

    float a = 3.*sin(iTime);
    float b = 3.*cos(iTime);

    float delta = (uv.x * uv.x) + (uv.y * uv.y * uv.y + a * uv.y + b);

    if (abs(delta) < .07) {
	  fragColor = vec4(vec3(1. - abs(delta) * 10.),1.0);
    } else if (delta < .0) {
      fragColor = vec4(vec3(0.5),1.0);
    } else {
      fragColor = vec4(vec3(0.0),1.0);
    }
}
