float posSin(float angle) {
    return sin(angle)/ 2.0 + 0.5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 position = abs(fragCoord.xy / iResolution.xy - 0.5);

    float angleOsc = (sin(iTime) + 256.0) * 16.0;

    float angle = atan(position.y / position.x) * angleOsc;


    fragColor = vec4( posSin(angle) );
}
