void mainImage( out vec4 color, in vec2 pixCoords )
{
    float zoom = (iResolution.x / 5.0);// * (1.0 + 0.2 * cos(2.0 * iTime));
    float cameraX = 5.0;  // + 4.0 * cos(iTime);
    float cameraY = 2.0; // + 4.0 * sin(iTime * 1.1);

    float x = (pixCoords.x / zoom*2.0) - cameraX;
    float y = (pixCoords.y / zoom*2.0) - cameraY;

    float lineSemiWidth = 0.05;

    if (abs(x) < lineSemiWidth || abs(y)<lineSemiWidth) {
		color = vec4(1.0, 0.0, 0.0, 1.0);
        return;
    }


    if (mod(x, 1.0) < lineSemiWidth || mod(y, 1.0) < lineSemiWidth) {
		color = vec4(0.5, 0.5, 0.5, 1.0);
        return;
    }

    float cX=0.35;
   float cY= 0.1 *cos(iTime);

   float newX;
   float newY;

    for(int i=0;i<=100;i++)
    {
        newX=x * x-y * y+cX;
        newY= 2.0 * x * y+cY;
        x=newX;
        y=newY;
        if(x*x+y*y>4.0)
        {
        color=vec4(1.0);
            break;

    }




    }

}
