void mainImage( out vec4 col, in vec2 p ) {
	float t = iTime;

    p /= iResolution.xy;

	col.rgb = vec3(0.0);

    col.r += 1.0 - texture(iChannel0, p).r;
    col.r -= texture(iChannel0, p).g;
    col.r -= texture(iChannel0, p).b;
    col.g += 1.0 - col.r;

    col.r += clamp(0.1, 0.2, col.r);

    col.r = smoothstep(0.28, 0.57, texture(iChannel0, p).g);

    col.g = 0.0;
    col.b = 0.0;

    col.g += smoothstep(0.7, 0.8, texture(iChannel0, p).g);


    col.r -= texture(iChannel1, p + t*0.012).g * 0.25;


}
