float edgePower = 2.75;
float colorSlammingPower = 3.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 origColor = texture(iChannel0, uv);
	//fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);
    vec2 uvU = uv + 0.005;
    vec4 neighborColor = texture(iChannel0, uvU);

    float len = distance(origColor.xyz, neighborColor.xyz) * edgePower;
    float pctEdge = len;// * 6.0;
    //pctEdge = 0.0;
    float pctColor = 1.0 - pctEdge;


    vec4 sv = floor(origColor * colorSlammingPower);
    vec4 bgColor = sv / colorSlammingPower;
    bgColor *= 2.0;

    if( uv.x < 0.6) 
	    fragColor = pctColor * bgColor + pctEdge * vec4(vec3(1.0 - len), 1.0);
   	else
        fragColor = origColor;
    //fragColor = vec4(vec3(len), 1.0);

}
