void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy - .5;

    float ang = atan(uv.y,uv.x);
    float len = 0.;
    for (int i = 0; i < 5; i++) {
        len = max(len,
                  texture(iChannel0,vec2(abs(ang)*.04+.5,float(i)*.05)).x);
    }
    len *= .5;

    len = abs(length(uv)-len);
    float ring = max(0.,1.-len*10.);
    len = len*30. + iTime*8.;
	fragColor = mix( vec4(.1),
                    vec4(sin(len),cos(len),-sin(len),1.)*.5+.5,
                    1.-ring);
}
