// animated variant of https://www.shadertoy.com/view/4t2cDm
// coefs from iq's https://www.shadertoy.com/view/lst3zf

#define T(U,l) texture( iChannel0, U ,l)

#define I(M,V) T( ( (U-V) * inverse(M) + C ) / 12. , -15.)

void mainImage( out vec4 O, vec2 u ) 
{
    float a = .1*cos(iTime), c=cos(a),s=sin(a);
    vec2 R = iResolution.xy,
         C = vec2( 5.5, 1 ),
         U = 12. * u/R - C;    
  // normalization: what should it be ? ( cause = resolution ? aspect ratio ? else ? )
    float k = 10.*T(U,15.).g; // .95 //mix(.962, .98, (R.x*R.y-512.*288.)/(1920.*1080.-512.*288.) ); 

    O = iFrame==0 || iMouse.z>0. ? vec4( step(length(U+U-1.),1.) )       // seed with a blob
         : max(  T( u/R ,0.)*.9 ,                             
           max( max( I( mat2( .01, 0    ,0  , .16), vec2( 0, 0   ) ),    // copy to stem ( .01 instead of 0 or degenerated)
                     I( .85 * mat2( c,s,-s,c), vec2( 0, 1.6 ) )   ),     // copy to next row
                max( I( mat2( .20,-.26,  .23, .22), vec2( 0, 1.6 ) ),    // copy to mirror side
                     I( mat2(-.15, .28,  .26, .24), vec2( 0,  .4 ) )   ) // copy to left side
           ) / k )        // note that max( T, ( I1+I2+I3+I4 ) / k' ) also works
	     * vec4(.995,1,.995,1);

    O = min(O,1.);        // for systems with normalization-by-MIPmap issues.
}
