bool c(vec2 uv, vec2 origin, float radius)
{
    float x2 = (uv.x+origin.x) * (uv.x+origin.x);
    float y2 = (uv.y+origin.y) * (uv.y+origin.y);

    if (x2 + y2 < radius)
    {
    	return true;
    }

    return false;
}

bool background(vec2 uv, vec2 origin, float radius)
{
    float x2 = (uv.x+origin.x) * (uv.x+origin.x);
    float y2 = (uv.y+origin.y) * (uv.y+origin.y);

    if (x2 + y2 < radius)
    {
    	return true;
    }

    return false;
}

bool onLine(vec2 uv, vec2 start, vec2 finish)
{   
    if (uv.x > start.x && uv.x < finish.x && uv.y > start.y && uv.y < finish.y)
    {
        return true;
    }

    return false;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    fragColor = vec4(0.,0.,0.,1.0);

    for (float x = 0.; x < 5.; x++)
    {
        for(float y = 0.; y < 50.; y++)
        {
            if (background(uv, vec2(-4.-(.05*x*cos(iTime))+ (0.3*y)+1.*sin(iTime), -.95 + 0.2*x), 0.005+.0005*sin(iTime)))
            {
                fragColor = vec4(0.2*x, 0.5+0.5*sin(iTime), 0.5+0.5*cos(iTime), 1.);
            }
        }
    }



    //head
    if( c(uv, vec2(-0.5, -0.5), .05+(0.001*iTime)) )
    {
		fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);
    }

    //eyes
    if (c(uv, vec2(-0.4, -.55), .0005))
    {
        fragColor = vec4(0.,0.,0.,1.0);
    }

    if (c(uv, vec2(-0.6, -.55), .0005))
    {
        fragColor = vec4(0.,0.,0.,1.0);
    }

    //mouth
    if (onLine(uv, vec2(0.35, 0.4), vec2(.65, .42)))
    {
        fragColor = vec4(0.,0.,0.,1.0);
    }

    //chin
    if (onLine(uv, vec2(0.45, 0.35), vec2(.55, .37)))
    {
        fragColor = vec4(0.,0.,0.,1.0);
    }

    //nose
    if (onLine(uv, vec2(0.475, 0.45), vec2(.525, .47)))
    {
        fragColor = vec4(0.,0.,0.,1.0);
    }
    if (onLine(uv, vec2(0.52, 0.45), vec2(.525, .57)))
    {
        fragColor = vec4(0.,0.,0.,1.0);
    }

        for (float x = -50.; x < 200.; x++)
    {
    	if (onLine(uv, vec2(0.1+0.01*x*.75+0.25*sin(iTime), 0.), vec2(0.1+(0.01*x*.75+0.25*sin(iTime))+0.001, 1.)))
        {
        	fragColor = vec4(0.,0.,0.,1.0);
        }

        if (onLine(uv, vec2(0.,0.1+0.01*x*.75+0.25*cos(iTime)), vec2(1.,0.1+(0.01*x*.75+0.25*cos(iTime))+0.001)))
        {
        	fragColor = vec4(0.,0.,0.,1.0);
        }
    }
}
